BEGIN { unshift @INC, '/usr/local/cpanel'; }

use Cpanel::RcsRecord          ();
use Cpanel::SafeRun::Simple    ();
use Cpanel::SafeRun::Errors    ();
use Cpanel::Rand               ();
use Cpanel::BinUtils           ();
use Cpanel::Config             ();
use Cpanel::Config::LoadCpConf ();
use Cpanel::Logger             ();
use Cpanel::OSSys              ();
use Cpanel::SafeFile           ();

$| = 1;

my $logger = Cpanel::Logger-&gt;new();

my ( $system, $nodename, $release, $version, $machine ) = Cpanel::OSSys::uname();

my $mount_bin = Cpanel::BinUtils::find_mount();
my $mount     = Cpanel::SafeRun::Simple::saferun($mount_bin);
chomp($mount);

my $aclsonfs = 0;
if ( $mount =~ /acl/ ) { $aclsonfs = 1; }

if ( !-x &quot;/bin/setfacl&quot; &amp;&amp; !-x &quot;/usr/bin/setfacl&quot; ) {
    acldeath(&quot;Unable to locate setfacl: your distribution may not support acls\n&quot;);
}

$release =~ /^(\d+\.\d+)/;
my $nversion = $1;

if ( $system =~ /linux/i   &amp;&amp; $nversion &lt; 2.6 ) { acldeath(&quot;You must be running at least a 2.6 kernel to use this script.&quot;); }
if ( $system =~ /freebsd/i &amp;&amp; $nversion &lt; 5 )   { acldeath(&quot;You must be running at least a 5.x kernel to use this script.&quot;); }

if ( $system =~ /freebsd/i ) {
    if ( !$aclsonfs ) {
        print &quot;!! No Filesystems are mounted with acl support !!\n\n&quot;;

        print &quot;Under freebsd you must use tunefs -a enable to turn on acls.\n&quot;;
        print &quot;Note: make sure your kernel supports acls before doing this.\n&quot;;
        print &quot;After you have remounted your file systems with acl support,\n&quot;;
        print &quot;re-run this script.\n&quot;;
        exit(1);
    }
    acltestandsetup();
}
else {
    setuplinuxfstab();
    acltestandsetup();
}

sub setuplinuxfstab {
    Cpanel::RcsRecord::rcsrecord(&quot;/etc/fstab&quot;);
    if ( !$aclsonfs ) {
        my $acltest = Cpanel::SafeRun::Errors::saferunallerrors( &quot;mount&quot;, &quot;-o&quot;, &quot;remount,acl&quot;, &quot;/&quot; );
        chomp($acltest);
        if ( $acltest ne &quot;&quot; ) {
            acldeath(&quot;System error: $acltest when testing for acl support.  Your kernel may not support POSIX acls\n&quot;);
        }
    }
    my $modtab = 0;
    my (@FSTAB);
    my (@REMOUNT);
    my $fstablock = Cpanel::SafeFile::safeopen( \*FT, &quot;+&lt;&quot;, &quot;/etc/fstab&quot; );
    if ( !$fstablock ) {
        $logger-&gt;die(&quot;Could not edit /etc/fstab&quot;);
    }
    while (&lt;FT&gt;) {
        chomp();
        if ( !/^[\s\t]*[\#\;]+/ ) {
            my (@FSLINE) = split( /[\s\t]+/, $_ );
            my @OPTS = split( /\,/, $FSLINE[3] );
            if ( $FSLINE[2] =~ /^ext\d+$/ &amp;&amp; !grep( /^acl$/, @OPTS ) ) {
                push( @OPTS, 'acl' );
                $FSLINE[3] = join( &quot;,&quot;, @OPTS );
                push( @FSTAB, join( &quot;\t&quot;, @FSLINE ) );
                push( @REMOUNT, $FSLINE[0] );
                $modtab = 1;
            }
            else {
                push( @FSTAB, $_ );
            }
        }
        else {
            push( @FSTAB, $_ );
        }
    }
    if ($modtab) {
        seek( FT, 0, 0 );
        print FT join( &quot;\n&quot;, @FSTAB ) . &quot;\n&quot;;
        truncate( FT, tell(FT) );
    }
    foreach (@REMOUNT) {
        system( &quot;mount&quot;, &quot;-o&quot;, &quot;remount&quot;, $_ );
    }
    Cpanel::SafeFile::safeclose( \*FT, $fstablock );
    Cpanel::RcsRecord::rcsrecord(&quot;/etc/fstab&quot;);
}

sub acltestandsetup {
    my $randfile = Cpanel::Rand::getranddata( 16, 0 );

    open( ACLTESTFILE, &quot;&gt;&quot;, &quot;/.acltest.${randfile}&quot; );
    close(ACLTESTFILE);

    my $acltest = Cpanel::SafeRun::Errors::saferunallerrors( &quot;setfacl&quot;, &quot;-m&quot;, &quot;user:nobody:r&quot;, &quot;--&quot;, &quot;/.acltest.${randfile}&quot; );
    my $acltest2 = Cpanel::SafeRun::Errors::saferunnoerror( &quot;getfacl&quot;, &quot;/.acltest.${randfile}&quot; );

    unlink(&quot;/.acltest.${randfile}&quot;);

    chomp($acltest);
    if ( $acltest ne &quot;&quot; ) {
        acldeath(&quot;System error: setfacl: $acltest when testing for acl support.  Your kernel may not support POSIX acls.\n&quot;);
    }
    chomp($acltest2);
    if ( $acltest2 !~ /nobody/i ) {
        acldeath(&quot;System error: setfacl did not set the permissions that were requested. Your kernel may not support POSIX acls.\n&quot;);
    }

    print &quot;ACLS are now on\n&quot;;

    setacls(1);
}

sub acldeath {
    my $err = shift;
    print &quot;ACLS are now off\n&quot;;
    setacls(0);
    die($err);
}

sub setacls {
    my $val        = shift;
    my $cpconf_ref = Cpanel::Config::LoadCpConf::loadcpconf();
    $cpconf_ref-&gt;{'acls'} = $val;
    Cpanel::Config::savecpconf($cpconf_ref);

    print &quot;Updating Permissions.....&quot;;
    if ( -e '/var/cpanel/fileprotect' ) {
        Cpanel::SafeRun::Errors::saferunnoerror('/scripts/enablefileprotect');
    }
    else {
        Cpanel::SafeRun::Errors::saferunnoerror('/scripts/disablefileprotect');
    }

    print &quot;Done\n&quot;;
}

