function RTrim(s,s_trim)
{
var iTemp = s.length -1;
while(iTemp > -1){
if(s.charAt(iTemp) == s_trim){
  return s.substring(0,iTemp);
  }
  iTemp--;
}return s;
}

function LTrim(s,s_trim)
{
var iTemp = 0;
while(iTemp < s.length){
if(s.charAt(iTemp) == s_trim){
  return s.substring(0,iTemp);
  }
  iTemp++;
}return s;
}

