my ( $system, $release, $machine ) = ( Cpanel::OSSys::uname() )[ 0, 2, 4 ];

my $hassigint = 0;
$SIG{'INT'} = sub {
    $hassigint = 1;
    print &quot;SIGINT received.  Cleaning up and halting operations.\n\n&quot;;
