sockopen:downloader:{
  if ($sockerr > 0) { echo **Invalid URL, check your parameters and retry. | return } 
  if (%down.t == http) { 
    set %down.time $ctime | .timerdown 0 1 down.ref
    dsock GET %down.rfile 
  }
  else { %down.ftplst = $true }
}
on 1:sockclose:downloader:{ 
  if (%down.t == ftp) { return }
  elseif ($lof(%down.lfile) < 500) {
    %down.hi = 0
    :loop
    inc %down.hi
    if (%down.hi > $lines(%down.lfile)) { goto sndmsg }
    else {
      %down.htmp = $read -l $+ %down.hi %down.lfile 
      if ((404 isin %down.htmp) && (not isin %down.htmp) && (found isin %down.htmp)) {
        decho Possible HTTP Error: %down.htmp 
        decho ^-to delete this file, press shift+f7
      }
      else { goto loop }
    }
  }
  else {
    :sndmsg
    decho %down.rfile Completed ( $+ $lof(%down.lfile) $+ bytes) in $duration($getdown.t)
    decho Average Speed: $calc($lof(%down.lfile) / $getdown.t) bps
  }
  sf5 -n
}
on 1:sockread:downloader:{
  if ($sockerr > 0) { decho ** .4Unknown error occured. | sf5 | halt } 
  elseif ((%down.t == ftp) && (%down.ftplst)) {
    :nextread1
    sockread %temp
    if ($sockbr == 0) { return }
    decho %temp
    down.proc %temp
    goto nextread1
  }
  else {
    :nextread
    sockread &temp  
    if ($sockbr == 0) { return }
    .bwrite %down.lfile -1 &temp
    goto nextread
  }
}
on 1:socklisten:downftpget:{ sockaccept downftpgetit }
on 1:sockread:downftpgetit:{
  if ($sockerr > 0) { decho ** .4Unknown error occured. | sf5 | halt }
  if (%down.ftpjlst) {
    sockread %down.lstit
    %down.filesiz = $gettok(%down.lstit, 5, 32)
    decho %down.lstit | unset %down.ftpjlst
    sockclose downftpget | sockclose downftpgetit
  }
  else {
    :nextread
    sockread &temp
    if ($sockbr == 0) { return }
    bwrite %down.lfile -1 &temp
    goto nextread
  }
}
