alias downloader {
  if ($window(@downloader) == $null) { window -e @downloader %downsave.downpos fixedsys }
  else { clear @downloader }
  if ($1 == $null) { halt }
  if ($sock(downloader, 0) > 0) {
    decho
    decho ** .4Download of %down.lfile in progress! (one file at a time.)
    decho ** .4To halt the progress of this file, press .2shift+f5
    decho | return
  }
  unset %down.*
  %down.lasturl = $1- 
  if ($left($1-, 7) == http://) { %down.p = 80 | %down.t = http | %down.s = $remove($1, http://) }
  elseif ($left($1, 6) == ftp://) {
    %down.s = $remove($1, ftp://)
    if ($count(%down.s, $chr($asc(:))) > 0) {
      %down.t = ftp
      if ($len($gettok(%down.s, 1, $asc(:))) < $len($gettok(%down.s, 2, $asc(:)))) {
        if ($chr($asc(@)) !isin %down.s) { decho Downloader - Must provide pass | sf5 | halt }
        %down.user = $gettok(%down.s, 1, $asc(:))
        %down.s = $remove(%down.s, %down.user) 
  %down.s = $right(%down.s, $calc($len(%down.s) - 1))
        %down.pass = $gettok(%down.s, 1, $asc(@))
        %down.s = $remove(%down.s, %down.pass)
        %down.s = $right(%down.s, $calc($len(%down.s) - 1))
	  if (%down.pass == $null) { decho Downloader - invalid parameters | halt }
      }
      else { %down.user = anonymous | %down.pass = leech@nowhere.org }
      if ($count(%down.s, $chr($asc(:))) > 0) {
        %down.p = $remove(%down.s, $gettok(%down.s, $count(%down.s, $chr($asc(:))), $asc(:)))
        %down.p = $right(%down.p, $calc($len(%down.p) - 1))
        %down.s = $remove(%down.s, %down.p)
        %down.s = $remove(%down.s, $chr($asc(:)))
      }
      else { %down.p = 21 }
    }
    else {
      %down.user = anonymous | %down.pass = Guest@downloader.mrc
      %down.t = ftp | %down.p = 21
    }
  }
  else { decho /downloader - invalid parameters: $1- | halt }
  %down.rfile = $remove(%down.s, $gettok(%down.s, 1, $asc(/)))
  %down.s = $gettok(%down.s, 1, $asc(/))
  decho Connection Type: $sr(%down.t)
  decho Server: $sr(%down.s)
  decho Port: $sr(%down.p)
  getdown.lfile
  sockopen downloader %down.s %down.p
}
alias dwn { downloader $1- }
alias -l down.proc {
  if ($1 == 220) { dsock USER %down.user }
  elseif ($1 == 331) { secho PASS $str($chr($asc(*)), $len(%down.pass)) | /sockwrite -n downloader PASS %down.pass }
  elseif (($1 == 230) && (log isin $1-)) { down.startftpj -l }
  elseif ($1 == 226) { down.startftpj }
  elseif (($1 isnum) && ($len($1) == 3) && ($left($1, 1) == 5) || ($left($1, 1) == 4)) { sf5 | halt }
  else { return }
}
alias -l down.startftpj {
  %down.sb = $rand(5, 9) | %down.sp = $rand(1, 20)
  %down.listenport = $replace($ip,.,$chr(44)) $+ , $+ %down.sb $+ , $+ %down.sp
  dsock PORT %down.listenport
  %down.lstport = $calc(256 * %down.sb + %down.sp)
  socklisten downftpget %down.lstport
  if ($1 == -l) { %down.ftpjlst = $true | dsock LIST %down.rfile }
  elseif ($sock(downftpget, 0) > 0) {
    %down.time = $ctime | .timerdown 0 1 down.ref
    dsock RETR %down.rfile
  }
}
alias -l down.ref {
  if ($window(@down-status) == $null) {
    window @down-status %downsave.down-statuspos fixedsys
    .aline @down-status . | .aline @down-status . | .aline @down-status . 
    .aline @down-status . | .aline @down-status . | .aline @down-status . 
    .aline @down-status . | .aline @down-status . | .aline @down-status . 
    titlebar @down-status %down.s
    titlebar @downloader Online - %down.lasturl
  }
  .rline @down-status 1 .2- DOWNLOADER GET SESSION -
  .rline @down-status 2 .3Remote:. %down.lasturl
  .rline @down-status 3 .3Local:. %down.lfile
  .rline @down-status 4 .2-
  .rline @down-status 5 .3Rcvd:. $lof(%down.lfile) bytes
  .rline @down-status 6 .3Size:. $iif((%down.filesiz != $null), %down.filesiz, Unknown) bytes
  .rline @down-status 7 .3Elapsed:. $duration($getdown.t)
  .rline @down-status 8 .2-
  if ((%down.t == ftp) && ($lof(%down.lfile) == %down.filesiz)) {
    decho %down.rfile Completed ( $+ %down.filesiz $+ bytes) in $duration($getdown.t)
    decho Average Speed: $calc(%down.filesiz / $getdown.t) bps
    sockclose downftpget | sockclose downftpgetit
    sockclose downloader | .timerdown off | unset %down.*
  }
  elseif ((%down.t == ftp) && ($sock(downftpgetit, 1).lr > 30)) { dsock NOOP }
  elseif ($sock(downftpgetit, 1).lr > 300) { sf5 }
}
