alias readout {
  %spy.target = $gettok(%report,1,58) | %spy.target = $gettok(%spy.target,1,33)
  if ((Mode == $2) && (%spy.target == %spy.nick)) { beep 2 | /echo -a 14,1.15:0:9( 14S15py 14B15ot 14C15onnected -15 %spy.nick 9)0:15:14.  | seek }
  if (Privmsg == $2) && ($chr(35) !isin $3) { invdetection $4- }
